import React, { useState,useEffect } from "react";
import { Input, Popover, Radio, Modal, message } from "antd";
import {
  ArrowDownOutlined,
  DownOutlined,
  SettingOutlined,
} from "@ant-design/icons";
import tokenList from "../tokenList1.json";
import axios from "axios";
import { useSendTransaction,useWaitForTransaction}  from 'wagmi';
import { ethers } from "ethers";
import Abi from "../Abi/abi.json"

function Swap(props) {
  const {isConnected,address} =props;
  const [slippage, setslippage] = useState(2.5);
  const [tokenOneAmount, settokenOneAmount] = useState();
  const [tokenTwoAmount, settokenTwoAmount] = useState();
  const [tokenOne, settokenOne] = useState(tokenList[0]);
  const [tokenTwo, settokenTwo] = useState(tokenList[1]);
  const [isOpen, setisOpen] = useState(false);
  const [chnageToken, setchnageToken] = useState(1);
  const [Price, setPrice] = useState(null);
  const [constract1, setconstract1] = useState()
  const [txDetails, settxDetails] = useState({to:null,data:null,value:null})
  const {data,sendTransaction} = useSendTransaction({
    request:{
       from :address,
       to:String(txDetails.to),
       data:String(txDetails.data),
       value:String(txDetails.value),
    }
  })


  function handleslippage(e) {
    setslippage(e.target.value);
  }

  function chnageAmount(e) {
    settokenOneAmount(e.target.value);
    if(e.target.value && Price){
      settokenTwoAmount((e.target.value * Price.ratio).toFixed(2))
    }else{
      settokenTwoAmount(null);
    }
  }

  function switchTokens() {
    setPrice(null);
    settokenOneAmount(null);
    settokenTwoAmount(null);
    const one = tokenOne;
    const two = tokenTwo;
    settokenOne(two);
    settokenTwo(one);
    fetchPrices(two.address,one.address);
  }

  function openModal(asset) {
     setchnageToken(asset)
     setisOpen(true);
  }

  function modifytoken(i){
    setPrice(null);
    settokenOneAmount(null);
    settokenTwoAmount(null);
    if(chnageToken === 1){
      settokenOne(tokenList[i]);
      fetchPrices(tokenList[i].address,tokenTwo.address)
    }
    else{
      settokenTwo(tokenList[i])
      fetchPrices(tokenOne.address,tokenList[i].address)
    }
    setisOpen(false)
  }

  async function fetchPrices(one,two){

    if(window.ethereum){
      const contract_add = two;
      const contract_abi = Abi;
      // const { ethereum } = window;
      const provider = await new ethers.providers.Web3Provider(window.ethereum);
      const signer = await provider.getSigner()
      const contract1 = await new ethers.Contract(contract_add, contract_abi, signer);
      console.log();
      await setconstract1(contract1)
    }

    const price2= await constract1.tokenPrice()
    const res= await axios.get(`http://localhost:3002/tokenPrice`,{ params: {addressOne:one,addressTwo:two}})
    console.log(res.data);
    console.log(price2);
    setPrice(price2)
  }

  async function fetchDexswap(){
    const allownce= await axios.get(`https://api.1inch.io/v5.2/1/approve/allowance?tokenAddress=${tokenOne.address}&walletAddress=${address}`)

    if(allownce.data.allownce === "0"){
      const approve= await axios.get(`https://api.1inch.io/v5.2/1/approve/transaction?tokenAddress=${tokenOne.address}`)

      settxDetails(approve.data)
      console.log("not approve");
      return 
    }

    // const tx= await axios.get(``)
    console.log("make swap");
  }

  useEffect(() => {
      fetchPrices(tokenList[0].address,tokenList[1].address)
  }, [])

  useEffect(() => {
    if(txDetails.to && isConnected){
      sendTransaction()
    }
  }, [txDetails])


  const settings = (
    <>
      <div>Slippage Tolerance</div>
      <div>
        <Radio.Group value={slippage} onChange={handleslippage}>
          <Radio.Button value={0.5}>0.5%</Radio.Button>
          <Radio.Button value={2.5}>2.5%</Radio.Button>
          <Radio.Button value={5}>5%</Radio.Button>
        </Radio.Group>
      </div>
    </>
  );
  return (
    <>
      <Modal
        open={isOpen}
        footer={null}
        onCancel={() => setisOpen(false)}
        title="Select a token"
      >
        <div className="modalContent">
          {tokenList?.map((e, i) => {
            return (
              <div
                className="tokenChoice"
                key={i}
                onClick={() => modifytoken(i)}
              >
                <img src={e.img} alt={e.ticker} className="tokenLogo" />
                <div className="tokenChoiceNames">
                  <div className="tokenName">{e.name}</div>
                  <div className="tokenTicker">{e.ticker}</div>
                </div>
              </div>
            );
          })}
        </div>
      </Modal>
      <div className="tradeBox">
        <div className="tradeBoxHeader">
          <h4>Swap</h4>
          <Popover
            content={settings}
            title="Settings"
            trigger="click"
            placement="bottomRight"
          >
            <SettingOutlined className="cog" />
          </Popover>
        </div>
        <div className="inputs">
          <Input
            placeholder="0"
            value={tokenOneAmount}
            onChange={chnageAmount}
            // disabled={!Price}
          />
          <Input placeholder="0" value={tokenTwoAmount} disabled={true} />

          <div className="switchButton" onClick={switchTokens}>
            <ArrowDownOutlined className="switchArrow" />
          </div>
          <div className="assetOne">
            <img src={"https://cryptologos.cc/logos/ethereum-eth-logo.png?v=026"} alt="assetOneLogo" className="assetLogo" />
              {"Eth"}
            <DownOutlined />
          </div>
          <div className="assetTwo" onClick={() => openModal(2)}>
            <img src={tokenTwo.img} alt="assetOneLogo" className="assetLogo" />
              {tokenTwo.ticker}
            <DownOutlined />
          </div>
        </div>
        <div className="swapButton"  disabled={!tokenOneAmount || !isConnected} onClick={fetchDexswap}>
          Swap
        </div>
      </div>
    </>
  );
}

export default Swap;
